# Transformando a entrada numa lista de listas para ser mutável
grade = [list(linha.strip()) for linha in entrada_texto.split('\n') if linha.strip()]
num_linhas = len(grade)
num_colunas = len(grade[0])

total_removidos = 0

while True:
    # Lista para armazenar as coordenadas dos rolos que serão removidos nesta rodada
    para_remover = []
    
    # Percorre toda a grade para identificar rolos removíveis
    for l in range(num_linhas):
        for c in range(num_colunas):
            if grade[l][c] == '@':
                vizinhos = 0
                # Verifica os 8 vizinhos
                for dl in [-1, 0, 1]:
                    for dc in [-1, 0, 1]:
                        if dl == 0 and dc == 0:
                            continue
                        
                        nl, nc = l + dl, c + dc
                        
                        if 0 <= nl < num_linhas and 0 <= nc < num_colunas:
                            # Conta apenas se for um rolo de papel
                            if grade[nl][nc] == '@':
                                vizinhos += 1
                
                # Regra: acessível se tiver menos de 4 vizinhos
                if vizinhos < 4:
                    para_remover.append((l, c))
    
    # Se não houver nada para remover, o processo termina
    if not para_remover:
        break
    
    # Atualiza o total
    total_removidos += len(para_remover)
    
    # Remove os rolos da grade (transforma em '.')
    for l, c in para_remover:
        grade[l][c] = '.'

print(f"Total de rolos removidos no final do processo: {total_removidos}")
